import React, { useEffect, useRef, useState } from "react";
import { useExternalScript } from "./helpers/ai-sdk/externalScriptsLoader";
import { getAiSdkControls } from "./helpers/ai-sdk/loader";
import './App.css';
import FaceTrackerComponent from "./components/FaceTrackerComponent";

import ScreenRecordingComponent from "./components/ScreenRecordingComponent";
import video from "./video.mp4";

function App() {
  const mphToolsState = useExternalScript("https://sdk.morphcast.com/mphtools/v1.0/mphtools.js");
  const aiSdkState = useExternalScript("https://ai-sdk.morphcast.com/v1.16/ai-sdk.js");
  const videoEl = useRef(undefined);
  const [isRecordingScreen, setIsRecordingScreen] = useState(false);
  const [isCameraAccessed, setIsCameraAccessed] = useState(false);

  const switchToRecordingOrAnalysis = () => {
    if (isRecordingScreen) {
      setIsRecordingScreen(false);
      startCameraAccess();
    } else {
      setIsRecordingScreen(true);
      stopCameraAccess();
    }
  };

  const startCameraAccess = async () => {
    if (!isRecordingScreen) {
      videoEl.current = document.getElementById("videoEl");
  
      if (aiSdkState === "ready" && mphToolsState === "ready") {
        const { source, start } = await getAiSdkControls();
  
        await source.useVideo({
          videoUrl: video, // Cambiado a la variable 'video'
          toVideoElement: document.getElementById("videoEl"),
        });
  
        await start();
        setIsCameraAccessed(true);
      }
    }
  };

  const stopCameraAccess = () => {
    if (isCameraAccessed) {
      // Detener el acceso a la c치mara
      const tracks = videoEl.current.srcObject.getTracks();
      tracks.forEach((track) => track.stop());
      videoEl.current.srcObject = null;
      setIsCameraAccessed(false);
    }
  };

  useEffect(() => {
    startCameraAccess();
  }, [aiSdkState, mphToolsState, startCameraAccess]); // Agregar startCameraAccess al array de dependencias

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
          {isRecordingScreen ? (
            <ScreenRecordingComponent switchToAnalysis={switchToRecordingOrAnalysis} />
          ) : (
            <>
              {/* Contenedor con posici칩n relativa */}
              <div style={{ position: "relative", width: "640px", height: "480px" }}>
                {/* Video y FaceTrackerComponent con posici칩n absoluta */}
                <video id="videoEl" style={{ width: "100%", height: "100%" }}></video>
                <FaceTrackerComponent videoEl={videoEl}></FaceTrackerComponent>
              </div>
              {/* Resto de los componentes */}
              {/* ... */}
            </>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
