import React, { useState, useEffect } from "react";
import "./componentCSS/ScreenRecordingComponent.css";

function ScreenRecordingComponent({ switchToAnalysis }) {
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [recordingCount, setRecordingCount] = useState(1);

  useEffect(() => {
    let chunks = [];

    const startRecording = async () => {
      try {
        const stream = await navigator.mediaDevices.getDisplayMedia({ video: true, audio: true });
        const recorder = new MediaRecorder(stream);

        recorder.ondataavailable = (e) => {
          if (e.data.size > 0) {
            chunks.push(e.data);
          }
        };

        recorder.onstop = () => {
          const mediaBlob = new Blob(chunks, { type: "video/webm" });
          const url = URL.createObjectURL(mediaBlob);
          const a = document.createElement("a");
          a.href = url;
          a.download = `captura_${recordingCount}.webm`;
          a.click();
          URL.revokeObjectURL(url);

          chunks = [];
          setRecordingCount((prevCount) => prevCount + 1);
        };

        recorder.start();

        setMediaRecorder(recorder);
        setMediaStream(stream);

        const interval = setInterval(() => {
          if (isRecording) {
            recorder.stop();
            recorder.start();
          }
        }, 10000); // 10 seconds interval

        return () => {
          clearInterval(interval);
          recorder.stop();
          stream.getTracks().forEach((track) => track.stop());
        };
      } catch (error) {
        console.error("Error al acceder a la pantalla o región:", error);
      }
    };

    if (isRecording) {
      startRecording();
    } else {
      if (mediaRecorder && mediaRecorder.state !== "inactive") {
        mediaRecorder.stop();
      }
      if (mediaStream) {
        mediaStream.getTracks().forEach((track) => track.stop());
      }
    }

    return () => {
      if (mediaRecorder && mediaRecorder.state !== "inactive") {
        mediaRecorder.stop();
      }
      if (mediaStream) {
        mediaStream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [isRecording, recordingCount]);

  const startRecordingHandler = () => {
    setIsRecording(true);
  };

  const stopRecordingHandler = () => {
    setIsRecording(false);
  };

  return (
    <div className="ScreenRecordingContainer">
      <h2>Interfaz de Grabación de Pantalla</h2>
      <video className="VideoPreview" id="preview" autoPlay muted></video>
      <div className="ButtonContainer">
        <button
          className="Button"
          id="startRecording"
          onClick={startRecordingHandler}
          disabled={isRecording}
        >
          Iniciar Grabación
        </button>
        <button
          className="Button"
          onClick={stopRecordingHandler}
          disabled={!isRecording}
        >
          Detener Grabación
        </button>
        <button className="Button" onClick={switchToAnalysis}>
          Cambiar a Análisis
        </button>
      </div>
    </div>
  );
}

export default ScreenRecordingComponent;
