import React, { useEffect, useRef, useState } from "react";
import { useExternalScript } from "./helpers/ai-sdk/externalScriptsLoader";
import { getAiSdkControls } from "./helpers/ai-sdk/loader";
import './App.css';

function App() {
  const [isRecordingScreen, setIsRecordingScreen] = useState(false);
  const [isCameraAccessed, setIsCameraAccessed] = useState(false);

  const switchToRecordingOrAnalysis = () => {
    if (isRecordingScreen) {
      setIsRecordingScreen(false);
      startCameraAccess();
    } else {
      setIsRecordingScreen(true);
      stopCameraAccess();
    }
  };

  const startCameraAccess = async () => {
    if (!isRecordingScreen) {
      videoEl.current = document.getElementById("videoEl");
      if (aiSdkState === "ready" && mphToolsState === "ready") {
        const { source, start } = await getAiSdkControls();
        await source.useCamera({
          toVideoElement: document.getElementById("videoEl"),
        });
        await start();
        setIsCameraAccessed(true);
      }
    }
  };

  const stopCameraAccess = () => {
    if (isCameraAccessed) {
      // Detener el acceso a la c치mara
      const tracks = videoEl.current.srcObject.getTracks();
      tracks.forEach((track) => track.stop());
      videoEl.current.srcObject = null;
      setIsCameraAccessed(false);
    }
  };

  useEffect(() => {
    startCameraAccess();
  }, [aiSdkState, mphToolsState]);

  return (
    <div className="App">
    
        
              <button
                onClick={switchToRecordingOrAnalysis}
                style={{
                  padding: "10px 20px",
                  backgroundColor: "#007bff",
                  color: "#fff",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  transition: "background-color 0.3s",
                  margin: "5px",
                }}
              >
                Cambiar a Grabaci칩n / An치lisis
              </button>
        </div>
   
  );
}

export default App;
